const express = require('express');
const app = express();

// Use Render's dynamic port or default to 3000
const PORT = process.env.PORT || 3000;

app.get('/', (req, res) => {
    res.send("API is running! ðŸŽ‰");
});

// Example API route
app.get('/wp-json/api/v1/verify/facebook', (req, res) => {
    res.json({ message: "Facebook verification API is working!" });
});

// Start the server
app.listen(PORT, () => {
    console.log(`Server running on port ${PORT}`);
});
